import React, { useState } from "react";
import { Navigation } from "../navigation";



export const Userchat = ()=>{
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  const handleSendMessage = () => {
    if (newMessage.trim() === "") return;

    const updatedMessages = [
      ...messages,
      { sender: "patient", text: newMessage },
    ];
    setMessages(updatedMessages);
    setNewMessage("");
  };

  return (
    <>
      <Navigation />
      <div style={styles.container}>
        <h1 style={styles.heading}>Chat with Doctor</h1>

        <div style={styles.centeredBox}>
          <div style={styles.chatBox}>
            {messages.map((message, index) => (
              <div key={index} style={styles.messageContainer}>
                <span
                  style={
                    message.sender === "patient"
                      ? styles.youLabel
                      : styles.doctorLabel
                  }
                >
                  {message.sender === "patient" ? "You:" : "Doctor:"}
                </span>
                <p style={styles.messageText}>{message.text}</p>
              </div>
            ))}
          </div>
        </div>

        <div style={styles.inputContainer}>
          <textarea
            rows="4"
            cols="50"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type your message..."
            style={styles.textArea}
          ></textarea>
          <br />
          <button onClick={handleSendMessage} style={styles.button}>
            Send Message
          </button>
        </div>
      </div>
    </>
  );
};

const styles = {
  container: {
    padding: "20px",
  },
  heading: {
    fontSize: "24px",
    marginBottom: "20px",
  },
  centeredBox: {
    width: "80%",
    margin: "0 auto", // Center the box horizontally
  },
  chatBox: {
    width: "80%",
    margin: "0 auto", // Center the chat box
    border: "1px solid #ccc",
    boxShadow: "0 0 10px rgba(255, 0, 0, 0.5)", // Red shadow
    backgroundColor: "#ADD8E6", // Light blue-green background color
    minHeight: "200px",
    padding: "10px",
    marginBottom: "20px",
    overflowY: "auto",
  },
  messageContainer: {
    marginBottom: "10px", // Adjust the margin as needed
  },
  
  
  youLabel: {
    fontWeight: "bold",
    color: "#2196F3",
  },
  doctorLabel: {
    fontWeight: "bold",
    color: "#4CAF50",
  },
  messageText: {
    margin: 0,
    paddingLeft: "10px",
  },
  inputContainer: {
    display: "flex",
    flexDirection: "column",
  },
  textArea: {
    marginBottom: "10px",
    padding: "10px",
  },
  button: {
    padding: "10px",
    backgroundColor: "#4CAF50",
    color: "#fff",
    cursor: "pointer",
    border: "none",
    borderRadius: "4px",
  },
};
