import React, { useState, useEffect } from 'react';
import './book.css';
import { Navigation } from '../navigation';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export const UserBook = () => {
  const [name, changename] = useState('');
  const [Doctor, changeDoctor] = useState('');
  const [Dis, changeDis] = useState('');
  const [err, changeerr] = useState('');
  const [allDoctors, setAllDoctors] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Dummy data for the list of doctors
    const dummyDoctors = [
      { id: 1, name: 'Dr. Smith' },
      { id: 2, name: 'Dr. Johnson' },
      // Add more dummy doctors as needed
    ];

    setAllDoctors(dummyDoctors);
  }, []);

  const save = async () => {
    if (name) {
      if (Doctor) {
        if (Dis) {
          let id;

          // Assume the length is always 0 for dummy data
          id = 0;

          const data = {
            appointmentId: id + 1,
            doctorName: Doctor,
            patientName: name,
            status: false,
            description: Dis,
          };

          console.log('Saving data:', data);

          // Dummy API call (no actual HTTP request)
          // You can replace this with your actual API call
          // await axios.post('http://localhost:1111/newAppoinment', data);

          changename('');
          changeDoctor('');
          changeDis('');
          navigate('/UserBook');
        } else {
          changeerr('Enter Description');
          setTimeout(hideSpam, 1000);
        }
      } else {
        changeerr('Select Doctor name');
        setTimeout(hideSpam, 1000);
      }
    } else {
      changeerr('Enter proper name');
      setTimeout(hideSpam, 1000);
    }
  };

  const hideSpam = () => {
    changeerr(null);
  };

  return (
    <>
      <Navigation />
      <div className="bcontainer">
        <div className="login">
          <h1>BOOK</h1>
          <p>Name</p>
          <input type="text" value={name} onChange={(e) => changename(e.target.value)} placeholder="Enter Name" />
          <p>Doctor Name</p>
          <select value={Doctor} onChange={(e) => changeDoctor(e.target.value)}>
            <option value="">Select Doctor</option>
            {allDoctors.map((doctor) => (
              <option key={doctor.id} value={doctor.name}>
                {doctor.name}
              </option>
            ))}
          </select>
          <p>Description</p>
          <input type="text" value={Dis} onChange={(e) => changeDis(e.target.value)} className="description" />
          <button onClick={save}>Register</button>
          {err ? <p className="eror">{err}</p> : null}
        </div>
      </div>
    </>
  );
};
