import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import './ForPass.css';

export const UserForgotPassword = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const navigate = useNavigate();

    useEffect(() => {}, [email, password]);

    const checkMail = (data) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        const isValid = emailRegex.test(data);
        return isValid;
    };

    const hideError = () => {
        setError(null);
    };

    const save = async () => {
        const data = {
            email: email,
            password: password
        };

        if (checkMail(email)) {
            if (password.length >= 5) {
                try {
                    const response = await axios.put("http://localhost:1111/updatePassword", data);
                    console.log(response.data);
                    navigate("/UserLogin");
                } catch (error) {
                    console.error('Error:', error);
                }
            } else {
                setError("Enter a password with at least 5 characters");
                setTimeout(hideError, 1000);
            }
        } else {
            setError("Enter a proper email");
            setTimeout(hideError, 1000);
        }
    };

    return (
        <div className="Fcontainer">
            <div className="Fpass">
                <hr />
                <p>Email</p>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Enter email"
                />
                <p>New Password</p>
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Enter password"
                />
                <button onClick={save}>Save Details</button>
                {error && <p className="error">{error}</p>}
            </div>
        </div>
    );
};
