import React, { useState } from 'react';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = () => {
    if (newMessage.trim() !== '') {
      const updatedMessages = [...messages, { text: newMessage, sender: 'user' }];
      setMessages(updatedMessages);
      setNewMessage('');
    }
  };

  return (
    <div>
      <h2>User  Chat</h2>

      <div style={chatContainerStyle}>
        {messages.map((message, index) => (
          <div key={index} style={messageStyle(message.sender)}>
            {message.text}
          </div>
        ))}
      </div>

      <div style={inputContainerStyle}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          style={inputStyle}
          placeholder="Type your message..."
        />
        <button onClick={handleSendMessage} style={buttonStyle}>
          Send
        </button>
      </div>
    </div>
  );
};

// Styles
const chatContainerStyle = {
  border: '1px solid #ccc',
  minHeight: '200px',
  padding: '10px',
  marginBottom: '10px',
  overflowY: 'scroll',
};

const messageStyle = (sender) => ({
  backgroundColor: sender === 'user' ? '#e6f7ff' : '#d9f9b1',
  padding: '8px',
  margin: '5px',
  borderRadius: '5px',
  maxWidth: '70%',
  alignSelf: sender === 'user' ? 'flex-start' : 'flex-end',
});

const inputContainerStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
};

const inputStyle = {
  flex: '1',
  padding: '8px',
  marginRight: '10px',
};

const buttonStyle = {
  padding: '8px',
  backgroundColor: '#4CAF50',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
};

export default Chat;
