import React, { useState, useEffect } from "react";
import { Navigation } from "../navigation";

export const DoctorChat=()=>{
    const handleSendMessage = () => {
        if (newMessage.trim() === "") return;
      
        const updatedMessages = [...messages, { sender: "patient", text: newMessage }];
        setMessages(updatedMessages);
        setNewMessage("");
      
        // Simulate doctor's reply after 2 seconds
        setTimeout(() => {
          const doctorReply = {
            sender: "doctor",
            text: "Yes, I'm available today. How can I help you?",
          };
          const updatedMessagesWithDoctorReply = [...updatedMessages, doctorReply];
          setMessages(updatedMessagesWithDoctorReply);
      
          // Simulate patient's auto-reply after 2 seconds
          setTimeout(() => {
            const patientAutoReply = {
              sender: "patient",
              text: "Thank you. I will connect with you after one week.",
            };
            const finalMessages = [...updatedMessagesWithDoctorReply, patientAutoReply];
            setMessages(finalMessages);
          }, 2000);
        }, 2000);
      };
      
      // ...
      
      <div key={index} style={styles.messageContainer}>
        <span
          style={
            message.sender === "patient"
              ? styles.patientLabel
              : styles.doctorLabel
          }
        >
          {message.sender === "patient" ? "Patient:" : "Doctor:"}
        </span>
        <p style={styles.messageText}>{message.text}</p>
      </div>
  
    return (
      <>
        <Navigation />
        <div style={styles.container}>
          <h1 style={styles.heading}>Chat with Doctor</h1>
  
          <div style={styles.centeredBox}>
            <div style={styles.chatBox}>
              {messages.map((message, index) => (
                <div key={index} style={styles.messageContainer}>
                  <span
                    style={
                      message.sender === "patient"
                        ? styles.youLabel
                        : styles.doctorLabel
                    }
                  >
                    {message.sender === "patient" ? "You:" : "Doctor:"}
                  </span>
                  <p style={styles.messageText}>{message.text}</p>
                </div>
              ))}
            </div>
          </div>
  
          <div style={styles.inputContainer}>
            <textarea
              rows="4"
              cols="50"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="Type your message..."
              style={styles.textArea}
            ></textarea>
            <br />
            <button onClick={handleSendMessage} style={styles.button}>
              Send Message
            </button>
          </div>
        </div>
      </>
    );
  };
  
  const styles = {
    container: {
      padding: "20px",
      backgroundColor: "#87CEEB", // SkyBlue background color
    },
    heading: {
      fontSize: "24px",
      marginBottom: "20px",
      color: "#4169E1", // RoyalBlue text color
    },
    centeredBox: {
      width: "80%",
      margin: "0 auto",
    },
    chatBox: {
      border: "1px solid #ccc",
      boxShadow: "0 0 90px rgba(0, 0, 255, 0.5)", // Blue shadow
      backgroundColor: "#87CEEB",
      minHeight: "200px",
      padding: "10px",
      marginBottom: "20px",
      overflowY: "auto",
    },
    messageContainer: {
      width: "80%",
      margin: "0 auto",
    },
    patientLabel: {
      fontWeight: "bold",
      color: "#1E90FF", // DodgerBlue text color
    },
    doctorLabel: {
      fontWeight: "bold",
      color: "#008080", // Teal text color
    },
    messageText: {
      margin: 0,
      paddingLeft: "10px",
    },
    inputContainer: {
      display: "flex",
      flexDirection: "column",
    },
    textArea: {
      width: "80%",
      margin: "0 auto",
      marginBottom: "10px",
      padding: "10px",
      boxShadow: "0 0 5px rgba(0, 0, 0, 0.2)",
    },
    button: {
      padding: "10px",
      textAlign: "center",
      backgroundColor: "#008080", // Teal button color
      color: "#fff",
      cursor: "pointer",
      border: "none",
      borderRadius: "4px",
      margin: "0 auto",
    },
  };
