import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Navigation } from "../../User/navigation";

const DoctorList = () => {
  const [hovered, setHovered] = useState(null);

  const doctors = [
    {
      id: 1,
      name: "Dr. John Doe",
      specialty: "Cardiologist",
      visitationDays: "Monday, Wednesday, Friday",
      visitationHours: "10:00 AM - 2:00 PM",
      consultationFee: "₹800",
      profilePhoto: "https://example.com/profile1.jpg",
    },
    // ... (Additional doctor data)
  ];

  const pageStyle = {
    backgroundImage: 'url("https://img.freepik.com/free-vector/abstract-medical-wallpaper-template-design_53876-61802.jpg?w=740&t=st=1703872185~exp=1703872785~hmac=de890d05056ce0c08b0cb0c62bfea578c1f6357b8294318ca27cffe68e736610")',
    minHeight: "100vh",
  };

  const gridContainer = {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
    gap: "20px",
    padding: "20px",
  };

  const cardStyle = {
    border: "5px solid #ccc",
    borderRadius: "10px",
    overflow: "hidden", // Ensure content stays within the card
    transition: "background-color 0.3s ease",
    cursor: "pointer",
    position: "relative",
    background: "#fff",
    boxShadow: "0px 5px 15px rgba(0, 0, 0, 0.1)",
  };

  const doctorPhotoStyle = {
    width: "100%",
    height: "200px",
    objectFit: "cover",
    borderRadius: "10px 10px 0 0",
  };

  const hoverStyle = {
    backgroundColor: "#f0f0f0",
    transform: "scale(1.05)",
  };

  const textContainerStyle = {
    padding: "10px",
  };

  const textStyles = {
    marginBottom: "10px",
    fontSize: "18px",
    color: "#333",
  };

  const buttonContainerStyle = {
    position: "absolute",
    bottom: "10px",
    left: "50%",
    transform: "translateX(-50%)",
    width: "100%",
    textAlign: "center",
  };

  const buttonStyle = {
    padding: "10px 20px",
    backgroundColor: "#007bff",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
  };

  return (
    <>
      <Navigation />
      <div style={pageStyle}>
        <div style={gridContainer}>
          {doctors.map((doctor) => (
            <div
              key={doctor.id}
              style={{
                ...cardStyle,
                ...(hovered === doctor.id ? hoverStyle : {}),
              }}
              onMouseEnter={() => setHovered(doctor.id)}
              onMouseLeave={() => setHovered(null)}
            >
              <img
                src={doctor.profilePhoto}
                alt={`Dr. ${doctor.name}`}
                style={doctorPhotoStyle}
              />
              <div style={textContainerStyle}>
                <h2 style={textStyles}>{doctor.name}</h2>
                <p style={textStyles}>Specialty: {doctor.specialty}</p>
                <p style={textStyles}>Visitation Days: {doctor.visitationDays}</p>
                <p style={textStyles}>Visitation Hours: {doctor.visitationHours}</p>
                <p style={textStyles}>Consultation Fee: {doctor.consultationFee}</p>
              </div>
              <div style={buttonContainerStyle}>
                <Link to="/UserBook" style={{ textDecoration: "none" }}>
                  <button
                    style={{ ...buttonStyle }}
                    onMouseEnter={(e) =>
                      (e.target.style.backgroundColor = "#0056b3")
                    }
                    onMouseLeave={(e) =>
                      (e.target.style.backgroundColor = "#007bff")
                    }
                  >
                    Book
                  </button>
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default DoctorList;
