import React from 'react';
import { Link } from 'react-router-dom';
import { Navigation } from '../../User/navigation';

const DoctorList = () => {
  // Sample data for doctors (replace it with your actual data)
  const doctors = [
    {
      id: 1,
      name: 'Dr. John Doe',
      image: 'doctor1.jpg', // Replace with the path to the doctor's image
      category: 'Cardiologist',
      availableTime: 'Mon - Fri, 9:00 AM - 5:00 PM',
    },
    {
      id: 2,
      name: 'Dr. Jane Smith',
      image: 'doctor2.jpg', // Replace with the path to the doctor's image
      category: 'Pediatrician',
      availableTime: 'Tue - Sat, 10:00 AM - 6:00 PM',
    },
    // Add more doctors as needed
  ];

  // Function to render a single card for a doctor
  const renderDoctorCard = (doctor) => {
    return (
      <>
      <div key={doctor.id} style={cardStyle}>
        <img src={doctor.image} alt={doctor.name} style={imageStyle} />
        <h3>{doctor.name}</h3>
        <p>{doctor.category}</p>
        <p>{doctor.availableTime}</p>
        <Link to="/Userbook" style={{ textDecoration: "none" }}>
        <button style={buttonStyle}>Book Now</button>
      </Link>
      </div>
      </>
    );
  };

  // Function to render a row of doctor cards
  const renderDoctorRow = (startIdx, endIdx) => {
    const rowStyle = {
      display: 'flex',
      justifyContent: 'space-around',
      marginBottom: '20px',
    };

    const rowCards = doctors.slice(startIdx, endIdx).map(renderDoctorCard);

    return <div key={startIdx} style={rowStyle}>{rowCards}</div>;
  };

  // Render the entire list of doctors in rows of cards
  const renderDoctorList = () => {
    const rowSize = 3;
    const rows = [];

    for (let i = 0; i < doctors.length; i += rowSize) {
      rows.push(renderDoctorRow(i, i + rowSize));
    }

    return <div>{rows}</div>;
  };

  // Styles
  const cardStyle = {
    border: '1px solid #ccc',
    padding: '20px',
    textAlign: 'center',
    width: '250px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  };

  const imageStyle = {
    width: '100%',
    borderRadius: '50%',
    marginBottom: '10px',
  };

  const buttonStyle = {
    backgroundColor: '#4CAF50',
    color: 'white',
    padding: '10px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  };

  return (
    <div>
      <h2>Doctor List</h2>
      {renderDoctorList()}
    </div>
  );
};

export default DoctorList;
