import React from 'react';
import { Link } from 'react-router-dom';
const Pharmacisthome = () => {
  const contentStyle = {
         backgroundColor: 'black',
         padding: '20px',
         textAlign: 'center',
         height: '20%',
         color: 'white',
       };

  
  const imageStyle = {
    width: '100%',
    height: '100%',
    maxHeight: '400px',
    objectFit: 'cover',
  };
  const imageStyle1 = {
    width: '100%',
    height: '50%',
    maxHeight: '250px',
    objectFit: 'cover',
  };
  const containerStyle = {
         position: 'relative',
         overflow: 'hidden',
         height: '100vh',

       };

  

  const cardsContainerStyle = {
    display: 'flex',
    justifyContent: 'space-around',
    marginTop: '20px',
  };

  const cardStyle = {
    width: '250px',
    padding: '20px',
    background: '#fff',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    textAlign: 'center',
    margin: '10px',
    height: '50%'
  };
  const button = {
        display: 'inline-block',
        marginTop: '20px',
        padding: '10px',
        backgroundColor: '#4CAF50', // Green button color
        color: '#fff',
        textDecoration: 'none', // Remove default link styling
        cursor: 'pointer',
        borderRadius: '5px',
         transition: 'background-color 0.3s',
      };
  return (
    <div style={containerStyle}>
      <img
        src="https://img.freepik.com/premium-photo/pharmaceutical-concept-stethoscope-pills-word-pharmacy_587895-928.jpg?w=2000"
        alt="Header"
        style={imageStyle}
      />
      
      <div style={contentStyle}>
        <h2>Pharma Care</h2>
        <p>
        Provides 24/7 all medicines.
        
       </p>
       <Link to="/Medicene" style={button}>
         See Medicines
       </Link>
      </div>
     
      <div style={cardsContainerStyle}>
        <div style={cardStyle}>
        <img
        src="https://t4.ftcdn.net/jpg/01/32/39/87/360_F_132398792_tu5ibyeSwJF0XlucHk4OXgmrtKl30Ylk.jpg" // Replace with your image URL
        alt="Header"
        style={imageStyle1}
      />
          <p>24/7 Available</p>
        </div>
        <div style={cardStyle}>
        <img
        src="https://media.istockphoto.com/id/1330728769/photo/woman-scientist-in-lab-look-at-science-microscope-medical-test-and-research-biology-chemistry.jpg?s=612x612&w=0&k=20&c=hwhF_EPzdnhZgiZtkHxgMfob9czynBB6-SEXMRIeKWk=" // Replace with your image URL
        alt="Header"
        style={imageStyle1}
      />
          <p>Laboratory</p>
        </div>
        <div style={cardStyle}>
        <img
        src="https://media.istockphoto.com/id/531935090/photo/medicine-pills.jpg?s=612x612&w=0&k=20&c=OLeavFQWHtFa1tZMcA_3b4sGLKgaUXmDC6u3i5DgX6A="
        alt="Header"
        style={imageStyle1}
      />
          <p>Refill Medicines</p>
        </div>
        
        
      </div>
    </div>
  );
};

export defaultÂ Pharmacisthome;
