import React, { useState, useEffect } from 'react';
import './book.css';
import { Navigation } from '../navigation';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export const UserBook = () => {
  const [name, changename] = useState('');
  const [Doctor, changeDoctor] = useState('');
  const [Dis, changeDis] = useState('');
  const [err, changeerr] = useState('');
  const [allDoctors, setAllDoctors] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the list of doctors when the component mounts
    const fetchDoctors = async () => {
      try {
        const response = await axios.get('http://localhost:1111/doctors');
        setAllDoctors(response.data);
      } catch (error) {
        console.error('Error fetching doctors:', error);
      }
    };

    fetchDoctors();
  }, []);

  const save = async () => {
    if (name) {
      if (Doctor) {
        if (Dis) {
          let id;

          const length = await axios
            .get('http://localhost:1111/appoinment')
            .then((response) => {
              id = response.data.length;
            })
            .catch((error) => {
              console.error('Error:', error);
            });

          const data = {
            appointmentId: id + 1,
            doctorName: Doctor,
            patientName: name,
            status: false,
            description: Dis,
          };

          await axios
            .post('http://localhost:1111/newAppoinment', data)
            .then((response) => {
              console.log(response.data);
              changename('');
              changeDoctor('');
              changeDis('');
              navigate('/UserBook');
            })
            .catch((error) => {
              console.error('Error:', error);
            });
        } else {
          changeerr('Enter Description');
          setTimeout(hideSpam, 1000);
        }
      } else {
        changeerr('Select Doctor name');
        setTimeout(hideSpam, 1000);
      }
    } else {
      changeerr('Enter proper name');
      setTimeout(hideSpam, 1000);
    }
  };

  const hideSpam = () => {
    changeerr(null);
  };

  return (
    <>
      <Navigation />
      <div className="bcontainer">
        <div className="login">
          <h1>BOOK</h1>
          <p>Name</p>
          <input type="text" value={name} onChange={(e) => changename(e.target.value)} placeholder="Enter Name" />
          <p>Doctor Name</p>
          <select value={Doctor} onChange={(e) => changeDoctor(e.target.value)}>
            <option value="">Select Doctor</option>
            {allDoctors.map((doctor) => (
              <option key={doctor.id} value={doctor.name}>
                {doctor.name}
              </option>
            ))}
          </select>
          <p>Description</p>
          <input type="text" value={Dis} onChange={(e) => changeDis(e.target.value)} className="description" />
          <button onClick={save}>Register</button>
          {err ? <p className="eror">{err}</p> : null}
        </div>
      </div>
    </>
  );
};
