import React from 'react';
import { Link } from 'react-router-dom';

const Pharmacisthome = () => {
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Welcome, Pharmacist!</h1>
      <p style={styles.subtitle}>You have access to medication records and prescriptions.</p>
      <Link to="/Medicene" style={styles.button}>
        Go to Medicine Page
      </Link>
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    padding: '50px',
    backgroundColor: '#98FB98', // Pale green background color
  },
  title: {
    fontSize: '24px',
    color: '#333',
  },
  subtitle: {
    fontSize: '18px',
    color: '#555',
  },
  button: {
    display: 'inline-block',
    marginTop: '20px',
    padding: '10px',
    backgroundColor: '#4CAF50', // Green button color
    color: '#fff',
    textDecoration: 'none', // Remove default link styling
    cursor: 'pointer',
    borderRadius: '5px',
    transition: 'background-color 0.3s',
  },
};

export default Pharmacisthome;
