import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Navigation } from "../../User/navigation";

const DoctorList = () => {
  const [hovered, setHovered] = useState(null);

  const doctors = [
    // ... (Your doctor data remains unchanged)
    {
      id: 1,
      name: "Dr. John Doe",
      specialty: "Cardiologist",
      visitationDays: "Monday, Wednesday, Friday",
      visitationHours: "10:00 AM - 2:00 PM",
      consultationFee: "₹800",
      profilePhoto: "https://example.com/profile1.jpg",
    },
    {
      id: 1,
      name: "Dr. John Doe",
      specialty: "Cardiologist",
      visitationDays: "Monday, Wednesday, Friday",
      visitationHours: "10:00 AM - 2:00 PM",
      consultationFee: "₹800",
      profilePhoto: "https://example.com/profile1.jpg",
    },
    {
      id: 1,
      name: "Dr. John Doe",
      specialty: "Cardiologist",
      visitationDays: "Monday, Wednesday, Friday",
      visitationHours: "10:00 AM - 2:00 PM",
      consultationFee: "₹800",
      profilePhoto: "https://example.com/profile1.jpg",
    },
    {
      id: 1,
      name: "Dr. John Doe",
      specialty: "Cardiologist",
      visitationDays: "Monday, Wednesday, Friday",
      visitationHours: "10:00 AM - 2:00 PM",
      consultationFee: "₹800",
      profilePhoto: "https://example.com/profile1.jpg",
    },
    {
      id: 1,
      name: "Dr. John Doe",
      specialty: "Cardiologist",
      visitationDays: "Monday, Wednesday, Friday",
      visitationHours: "10:00 AM - 2:00 PM",
      consultationFee: "₹800",
      profilePhoto: "https://example.com/profile1.jpg",
    },
    // ... (Additional doctor data)
  ];

  const pageStyle = {
    backgroundImage: 'url("https://img.freepik.com/free-vector/abstract-medical-wallpaper-template-design_53876-61802.jpg?w=740&t=st=1703872185~exp=1703872785~hmac=de890d05056ce0c08b0cb0c62bfea578c1f6357b8294318ca27cffe68e736610")',
    minHeight: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  };

  const cardStyle = {
    // Update card styles, remove background image and add relative positioning
    border: "5px solid #ccc",
    borderRadius: "5px",
    padding: "20px",
    margin: "10px",
    width: "300px",
    textAlign: "left",
    transition: "background-color 0.3s ease",
    cursor: "pointer",
    position: "relative", // Add relative positioning
    overflow: "hidden", // Ensure content stays within the card
  };

  const doctorPhotoStyle = {
    width: "100%",
    height: "auto",
    borderRadius: "5px 5px 0 0", // Rounded corners only at the top
  };

  const gridContainer = {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
    gap: "20px",
    padding: "20px",
  };

  const hoverStyle = {
    backgroundColor: "#f0f0f0",
    transform: "scale(1.05)",
  };

  const buttonStyle = {
    padding: "10px 20px",
    backgroundColor: "#007bff",
    color: "white",
    border: "none",
    borderRadius: "0 0 5px 5px", // Rounded corners only at the bottom
    cursor: "pointer",
    transition: "background-color 0.3s ease",
    width: "100%", // Make the button full width
    marginTop: "10px", // Add margin at the top
  };

  return (
    <>
      <Navigation />
      <div style={pageStyle}>
        <div style={gridContainer}>
          {doctors.map((doctor) => (
            <div
              key={doctor.id}
              style={{
                ...cardStyle,
                ...(hovered === doctor.id ? hoverStyle : {}),
              }}
              onMouseEnter={() => setHovered(doctor.id)}
              onMouseLeave={() => setHovered(null)}
            >
              {/* Display doctor's photo */}
              <img
                src={doctor.profilePhoto}
                alt={`Dr. ${doctor.name}`}
                style={doctorPhotoStyle}
              />
              <h2>{doctor.name}</h2>
              <p>Specialty: {doctor.specialty}</p>
              <p>Visitation Days: {doctor.visitationDays}</p>
              <p>Visitation Hours: {doctor.visitationHours}</p>
              <Link to="/UserBook" style={{ textDecoration: "none" }}>
                <button
                  style={{ ...buttonStyle }}
                  onMouseEnter={(e) =>
                    (e.target.style.backgroundColor = "#0056b3")
                  }
                  onMouseLeave={(e) =>
                    (e.target.style.backgroundColor = "#007bff")
                  }
                >
                  Book
                </button>
              </Link>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default DoctorList;
