import React, { useEffect, useState } from "react";
import "./ForPass.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import HomeIcon from '@mui/icons-material/Home';
import { useHistory } from 'react-router-dom'; 

export const UserForgotPassword = () => {
  const [email, changeemail] = useState("");
  const [password, changepassword] = useState("");
  const [err, changeerr] = useState("");
  const navigate = useNavigate("");
  const history = useHistory();

  const handleHomeClick = () => {
    // Navigate to the home page or the desired route
    history.push('/');
  };


  useEffect(() => {}, [email, password]);

  const checkMail = (data) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const isValid = emailRegex.test(data);
    return isValid;
  };

  const hideSpam = () => {
    changeerr(null);
  };

  const save = async () => {
    const data = {
      email: email,
      password: password,
    };

    console.log(data,"-------------------------------------------------------------");

    if (checkMail(email)) {
      if (password.length >= 5) {
        await axios
          .put("http://localhost:1111/updateUserPassword", data)
          .then((response) => {
            console.log(response.data);
            navigate("/UserLogin");
          })
          .catch((error) => {
            console.error("Error:", error);
          });
        // navigate("/Doctorlogin")
      } else {
        changeerr("enter password atleast 5 character");
        setTimeout(hideSpam, 1000);
      }
    } else {
      changeerr("enter proper email");
      setTimeout(hideSpam, 1000);
    }
  };
  return (
    <div className="Fcontainer">
      <div className="Fpass">
      <div>
      {/* Use HomeIcon wherever you need it */}
      <HomeIcon style={{ fontSize: 30 }} onClick={handleHomeClick}/>
      <p>This is your component content.</p>
    </div>
      <h2>FORGOT-PASSWORD</h2>
        
        <p>Email</p>
        <input
          type="email"
          value={email}
          onChange={(e) => changeemail(e.target.value)}
          placeholder="Enter value"
        />
        <p>New Password</p>
        <input
          type="Password"
          value={password}
          onChange={(e) => changepassword(e.target.value)}
          placeholder="Enter Password"
        />
        {/* <Link to="/Doctorlogin"> */}
        <button onClick={save}>save the details</button>
        {/* </Link> */}
        {err ? <p className="error">{err}</p> : null}
      </div>
    </div>
  );
};
