import React from 'react';
import { Navigation } from './navigation';


const doctorList = () => {
    const [hovered, setHovered] = React.useState(null);
  const doctors = [
    {
      id: 1,
      name: 'Dr. John Doe',
      specialty: 'Cardiologist',
      visitationDays: 'Monday, Wednesday, Friday',
      visitationHours: '10:00 AM - 2:00 PM',
    },
    {
      id: 2,
      name: 'Dr. Sarah Smith',
      specialty: 'Pediatrician',
      visitationDays: 'Tuesday, Thursday',
      visitationHours: '9:00 AM - 1:00 PM',
    },
    {
        id: 3,
        name: 'Dr. Sethu Madhavan',
        specialty: 'Dentist',
        visitationDays: 'Tuesday, Thursday',
        visitationHours: '10:00 AM - 1:00 PM',
      },
      {
        id: 4,
        name: 'Dr. Bathrinath',
        specialty: 'Pediatrician',
        visitationDays: 'Tuesday, Thursday, Friday',
        visitationHours: '11:00 AM - 1:00 PM',
      },
      {
        id: 5,
        name: 'Dr. Ashok',
        specialty: 'General',
        visitationDays: 'Tuesday, Wednesday, Thursday',
        visitationHours: '4:00 PM - 6:00 PM',
      },
      {
        id: 6,
        name: 'Dr. Balaji',
        specialty: 'Pediatrician',
        visitationDays: 'Monday, Tuesday, Wednesday',
        visitationHours: '5:00 AM - 7:00 PM',
      },
    // Add more doctor details as needed
  ];

  const cardStyle = {
    border: '1px solid #ccc',
    borderRadius: '5px',
    padding: '20px',
    margin: '10px',
    width: '300px',
    textAlign: 'left',
    transition: 'background-color 0.3s ease', // Transition for hover effect
    cursor: 'pointer',
  };

  const gridContainer = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '20px',
    padding: '20px',
  };

  const hoverStyle = {
    backgroundColor: '#f0f0f0', // Background color on hover
  };
  return (
    <>
    <Navigation />
    <div style={gridContainer}>
      {doctors.map((doctor) => (
        <div
            key={doctor.id}
            style={{
              ...cardStyle,
              ...(hovered === doctor.id ? hoverStyle : {}),
            }}
            onMouseEnter={() => setHovered(doctor.id)}
            onMouseLeave={() => setHovered(null)}
          >
          <h2>{doctor.name}</h2>
          <p>Specialty: {doctor.specialty}</p>
          <p>Visitation Days: {doctor.visitationDays}</p>
          <p>Visitation Hours: {doctor.visitationHours}</p>
        </div>
      ))}
    </div>
    </>
  );
};

export default DoctorList;
