import React, { useState } from "react";
import { Navigation } from "../navigation";



export const Userchat = ()=>{
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [isChatEnding, setIsChatEnding] = useState(false);

  const handleSendMessage = () => {
    if (newMessage.trim() === "") return;

    const updatedMessages = [
      ...messages,
      { sender: "patient", text: newMessage },
    ];
    setMessages(updatedMessages);
    setNewMessage("");

    // Simulate auto-reply after 2 seconds
    setTimeout(() => {
      const autoReply = {
        sender: "doctor",
        text: "Thank you for your message. I'll get back to you shortly.",
      };
      setMessages([...updatedMessages, autoReply]);
    }, 2000);
  };

  useEffect(() => {
    if (isChatEnding) {
      // Simulate ending the chat after 3 days
      const chatEndTimer = setInterval(() => {
        setMessages((prevMessages) => [
          ...prevMessages,
          {
            sender: "doctor",
            text: "Chat with me after 3 days.",
          },
        ]);
      }, 86400000 * 3); // 86400000 milliseconds in a day

      return () => clearInterval(chatEndTimer);
    }
  }, [isChatEnding]);

  return (
    <>
      <Navigation />
      <div style={styles.container}>
        <h1 style={styles.heading}>Chat with Doctor</h1>

        <div style={styles.centeredBox}>
          <div style={styles.chatBox}>
            {messages.map((message, index) => (
              <div key={index} style={styles.messageContainer}>
                <span
                  style={
                    message.sender === "patient"
                      ? styles.youLabel
                      : styles.doctorLabel
                  }
                >
                  {message.sender === "patient" ? "You:" : "Doctor:"}
                </span>
                <p style={styles.messageText}>{message.text}</p>
              </div>
            ))}
          </div>
        </div>

        <div style={styles.inputContainer}>
          <textarea
            rows="4"
            cols="50"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type your message..."
            style={styles.textArea}
            disabled={isChatEnding}
          ></textarea>
          <br />
          <button
            onClick={handleSendMessage}
            style={styles.button}
            disabled={isChatEnding}
          >
            Send Message
          </button>
        </div>
      </div>
    </>
  );
};

  
  const styles = {
    pageContainer: {
      backgroundColor: "#ADD8E6", // Blue-yellow mixed color
      minHeight: "100vh", // Full height of the viewport
    },
    container: {
      padding: "20px",
    },
    heading: {
      fontSize: "24px",
      marginBottom: "20px",
    },
    centeredBox: {
      width: "80%",
      margin: "0 auto", // Center the box horizontally
    },
    chatBox: {
      border: "1px solid #ccc",
      boxShadow: "0 0 90px rgba(255, 0, 0, 0.5)", // Red shadow
      backgroundColor: "#ADD8E6", // Light blue-green background color
      minHeight: "200px",
      padding: "10px",
      marginBottom: "20px",
      overflowY: "auto",
    },
    messageContainer: {
      width: "80%",
      margin: "0 auto", // Center the message container
    },
    youLabel: {
      fontWeight: "bold",
      color: "#2196F3",
    },
    doctorLabel: {
      fontWeight: "bold",
      color: "#4CAF50",
    },
    messageText: {
      margin: 0,
      paddingLeft: "10px",
    },
    inputContainer: {
      display: "flex",
      flexDirection: "column",
    },
    textArea: {
      width: "80%", // Set the width to match the chat box
      margin: "0 auto", // Center the text area
      marginBottom: "10px",
      padding: "10px",
      boxShadow: "0 0 5px rgba(0, 0, 0, 0.2)", // Subtle shadow
    },
    button: {
      width: "80%", // Set the width to match the chat box
      margin: "0 auto", // Center the button
      padding: "10px",
      textAlign: "center",
      backgroundColor: "#4CAF50",
      color: "#fff",
      cursor: "pointer",
      border: "none",
      borderRadius: "4px",
    },
  };
