// Chat.js

import React, { useState } from 'react';

const Chat = ({ role }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = () => {
    if (newMessage.trim() === '') return;

    const updatedMessages = [...messages, { role, text: newMessage }];
    setMessages(updatedMessages);
    setNewMessage('');
  };

  return (
    <div style={styles.container}>
      <div style={styles.messagesContainer}>
        {messages.map((message, index) => (
          <div
            key={index}
            style={message.role === 'patient' ? styles.patientMessage : styles.doctorMessage}
          >
            {message.text}
          </div>
        ))}
      </div>
      <div style={styles.inputContainer}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          style={styles.input}
          placeholder="Type your message..."
        />
        <button onClick={handleSendMessage} style={styles.button}>
          Send
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '400px',
    margin: 'auto',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '5px',
  },
  messagesContainer: {
    minHeight: '200px',
    marginBottom: '10px',
    overflowY: 'auto',
  },
  doctorMessage: {
    background: '#4CAF50', // Green background color for doctor's messages
    color: '#fff',
    padding: '8px',
    borderRadius: '5px',
    marginBottom: '8px',
  },
  patientMessage: {
    background: '#2196F3', // Blue background color for patient's messages
    color: '#fff',
    padding: '8px',
    borderRadius: '5px',
    marginBottom: '8px',
  },
  inputContainer: {
    display: 'flex',
    alignItems: 'center',
  },
  input: {
    flex: 1,
    padding: '8px',
    marginRight: '8px',
    borderRadius: '5px',
    border: '1px solid #ccc',
  },
  button: {
    padding: '8px 12px',
    borderRadius: '5px',
    background: '#4CAF50', // Green button color
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
  },
};

export default Chat;
