import React, { useEffect, useState } from "react";
import "./ForPass.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export const UserForgotPassword = () => {
  const [email, changeemail] = useState("");
  const [password, changepassword] = useState("");
  const [err, changeerr] = useState("");
  const navigate = useNavigate("");

  useEffect(() => {}, [email, password]);

  const checkMail = (data) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const isValid = emailRegex.test(data);
    return isValid;
  };

  const hideSpam = () => {
    changeerr(null);
  };

  const save = async () => {
    const data = {
      email: email,
      password: password,
    };

    console.log(
      data,
      "-------------------------------------------------------------"
    );

    if (checkMail(email)) {
      if (password.length >= 5) {
        await axios
          .put("http://localhost:1111/updateUserPassword", data)
          .then((response) => {
            console.log(response.data);
            navigate("/UserLogin");
          })
          .catch((error) => {
            console.error("Error:", error);
          });
        // navigate("/Doctorlogin")
      } else {
        changeerr("enter password atleast 5 character");
        setTimeout(hideSpam, 1000);
      }
    } else {
      changeerr("enter proper email");
      setTimeout(hideSpam, 1000);
    }
  };
  return (
    <div className="Fcontainer">
      <div className="Fpass">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width="16"
          height="16"
          fill="currentColor"
          class="bi bi-house-dash-fill"
          viewBox="0 0 16 16"
        >
          <path d="M8.707 1.5a1 1 0 0 0-1.414 0L.646 8.146a.5.5 0 0 0 .708.708L8 2.207l6.646 6.647a.5.5 0 0 0 .708-.708L13 5.793V2.5a.5.5 0 0 0-.5-.5h-1a.5.5 0 0 0-.5.5v1.293z" />
          <path d="m8 3.293 4.712 4.712A4.5 4.5 0 0 0 8.758 15H3.5A1.5 1.5 0 0 1 2 13.5V9.293l6-6Z" />
          <path d="M12.5 16a3.5 3.5 0 1 0 0-7 3.5 3.5 0 0 0 0 7M11 12h3a.5.5 0 0 1 0 1h-3a.5.5 0 1 1 0-1" />
        </svg>
        <h2>FORGOT-PASSWORD</h2>

        <p>Email</p>
        <input
          type="email"
          value={email}
          onChange={(e) => changeemail(e.target.value)}
          placeholder="Enter value"
        />
        <p>New Password</p>
        <input
          type="Password"
          value={password}
          onChange={(e) => changepassword(e.target.value)}
          placeholder="Enter Password"
        />
        {/* <Link to="/Doctorlogin"> */}
        <button onClick={save}>save the details</button>
        {/* </Link> */}
        {err ? <p className="error">{err}</p> : null}
      </div>
    </div>
  );
};
