import React, { useEffect, useState } from "react";
import './ForPass.css';
import { useNavigate } from "react-router-dom";
import axios from 'axios';

const UserForgotPassword = () => {
  const [email, changeEmail] = useState("");
  const [password, changePassword] = useState("");
  const [err, changeErr] = useState("");
  const navigate = useNavigate();

  useEffect(() => {}, [email, password]);

  const checkMail = (data) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const isValid = emailRegex.test(data);
    return isValid;
  };

  const hideSpam = () => {
    changeErr(null);
  };

  const save = async () => {
    const data = {
      email: email,
      password: password
    };

    if (checkMail(email)) {
      if (password.length >= 5) {
        try {
          const response = await axios.put("http://localhost:1111/updatePassword", data);
          console.log(response.data);
          navigate("/UserLogin");
        } catch (error) {
          console.error('Error:', error);
        }
      } else {
        changeErr("Enter a password of at least 5 characters");
        setTimeout(hideSpam, 1000);
      }
    } else {
      changeErr("Enter a proper email");
      setTimeout(hideSpam, 1000);
    }
  };

  return (
    <div className="Fcontainer">
      <div className="Fpass">
        <hr />
        <p>Email</p>
        <input
          type="email"
          value={email}
          onChange={(e) => changeEmail(e.target.value)}
          placeholder="Enter email"
        />
        <p>New Password</p>
        <input
          type="password"
          value={password}
          onChange={(e) => changePassword(e.target.value)}
          placeholder="Enter password"
        />
        <button onClick={save}>Save Details</button>
        {err ? <p className="error">{err}</p> : null}
      </div>
    </div>
  );
};

export default UserForgotPassword;
